// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import { console2 } from "forge-std/console2.sol";
import { BaseScript } from "../../base/Base.s.sol";
import { ForceTransferBera } from "../logic/ForceTransferBera.sol";
import { BGTDeployer } from "../logic/BGTDeployer.sol";
import { BGT_ADDRESS } from "../POLAddresses.sol";
import { BGT_SALT } from "../POLSalts.sol";

contract DeployBGTScript is BaseScript, BGTDeployer, ForceTransferBera {
    uint256 internal constant TESTNET_RESERVE_BERA_AMOUNT = 30e6 ether; // 30M

    function run() public broadcast {
        address bgt = deployBGT(msg.sender, BGT_SALT);
        _checkDeploymentAddress("BGT", bgt, BGT_ADDRESS);

        // NOTE: DOUBLE CHECK TESTNET ENV FLAG BEFORE DEPLOYMENT
        if (_isTestnet) {
            // Create a reserve of BERA for the BGT contract
            forceSafeTransferBERATo(BGT_ADDRESS, TESTNET_RESERVE_BERA_AMOUNT);
            require(BGT_ADDRESS.balance == TESTNET_RESERVE_BERA_AMOUNT, "BERA reserve not transferred to BGT contract");
        }
    }
}

script/pol/logic/BGTDeployer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import { BGT } from "src/pol/BGT.sol";
import { Create2Deployer } from "src/base/Create2Deployer.sol";

abstract contract BGTDeployer is Create2Deployer {
    /// @notice Deploy BGT contract
    function deployBGT(address owner, uint256 bgtSalt) internal returns (address) {
        BGT bgt = BGT(deployWithCreate2(bgtSalt, type(BGT).creationCode));
        bgt.initialize(owner);

        require(keccak256(bytes(bgt.CLOCK_MODE())) == keccak256("mode=timestamp"), "BGT CLOCK_MODE is incorrect");

        return address(bgt);
    }
}

script/pol/logic/ForceTransferBera.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import { console2 } from "forge-std/console2.sol";

abstract contract ForceTransferBera {
    /// @notice Force transfer BERA to BGT contract using SELFDESTRUCT opcode.
    function forceSafeTransferBERATo(address to, uint256 amount) public {
        require(msg.sender.balance >= amount, "Sender balance is less than the amount to transfer");
        // The BGT contract doesn't have `fallback` or `receive` functions, so we need to use `forceSafeTransferETH` to
        // send BERA to it.
        // SafeTransferLib.forceSafeTransferETH(address(bgt), amount);
        assembly ("memory-safe") {
            mstore(0x00, to) // Store the address in scratch space.
            mstore8(0x0b, 0x73) // Opcode `PUSH20`.
            mstore8(0x20, 0xff) // Opcode `SELFDESTRUCT`.
            if iszero(create(amount, 0x0b, 0x16)) { revert(0, 0) }
        }
        console2.log("Sent %d BERA to %s", amount, to);
    }
}

