src/base/StakingRewards.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { FixedPointMathLib } from "solady/src/utils/FixedPointMathLib.sol";

import { Utils } from "../libraries/Utils.sol";
import { IStakingRewards } from "./IStakingRewards.sol";

/// @title StakingRewards
/// @author Berachain Team
/// @notice This is a minimal implementation of staking rewards logic to be inherited.
/// @dev This contract is modified and abstracted from the stable and tested:
/// https://github.com/Synthetixio/synthetix/blob/develop/contracts/StakingRewards.sol
abstract contract StakingRewards is Initializable, IStakingRewards {
    using Utils for bytes4;
    using SafeERC20 for IERC20;

    /// @notice Struct to hold account data.
    /// @param balance The balance of the staked tokens.
    /// @param unclaimedReward The amount of unclaimed rewards.
    /// @param rewardsPerTokenPaid The amount of rewards per token paid, scaled by PRECISION.
    struct Info {
        uint256 balance;
        uint256 unclaimedReward;
        uint256 rewardsPerTokenPaid;
    }

    uint256 internal constant PRECISION = 1e18;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                          STORAGE                           */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @notice ERC20 token which users stake to earn rewards.
    IERC20 public stakeToken;

    /// @notice ERC20 token in which rewards are denominated and distributed.
    IERC20 public rewardToken;

    /// @notice The reward rate for the current reward period scaled by PRECISION.
    uint256 public rewardRate;

    /// @notice The amount of undistributed rewards scaled by PRECISION.
    uint256 public undistributedRewards;

    /// @notice The last updated reward per token scaled by PRECISION.
    uint256 public rewardPerTokenStored;

    /// @notice The total supply of the staked tokens.
    uint256 public totalSupply;

    /// @notice The end of the current reward period, where we need to start a new one.
    uint256 public periodFinish;

    /// @notice The time over which the rewards will be distributed. Current default is 7 days.
    uint256 public rewardsDuration;

    /// @notice The last time the rewards were updated.
    uint256 public lastUpdateTime;

    /// @notice The mapping of accounts to their data.
    mapping(address account => Info) internal _accountInfo;

    /// @dev This gap is used to prevent storage collisions.
    uint256[50] private __gap;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                         INITIALIZER                        */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @dev Must be called by the initializer of the inheriting contract.
    /// @param _stakingToken The address of the token that users will stake.
    /// @param _rewardToken The address of the token that will be distributed as rewards.
    /// @param _rewardsDuration The duration of the rewards cycle.
    function __StakingRewards_init(
        address _stakingToken,
        address _rewardToken,
        uint256 _rewardsDuration
    )
        internal
        onlyInitializing
    {
        stakeToken = IERC20(_stakingToken);
        rewardToken = IERC20(_rewardToken);
        rewardsDuration = _rewardsDuration;
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                         MODIFIERS                          */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    modifier updateReward(address account) {
        _updateReward(account);
        _;
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                  STATE MUTATING FUNCTIONS                  */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @notice Notifies the staking contract of a new reward transfer.
    /// @param reward The quantity of reward tokens being notified.
    /// @dev Only authorized notifiers should call this method to avoid griefing or false notifications.
    function _notifyRewardAmount(uint256 reward) internal virtual updateReward(address(0)) {
        reward = reward * PRECISION;

        if (totalSupply != 0 && block.timestamp < periodFinish) {
            reward += _computeLeftOverReward();
        }

        undistributedRewards += reward;
        _checkRewardSolvency();

        if (totalSupply != 0) {
            _setRewardRate();
            lastUpdateTime = block.timestamp;
        }

        emit RewardAdded(reward);
    }

    /// @notice Check if the rewards are solvent.
    /// @dev Inherited contracts may override this function to implement custom solvency checks.
    function _checkRewardSolvency() internal view virtual {
        if (undistributedRewards / PRECISION > rewardToken.balanceOf(address(this))) {
            InsolventReward.selector.revertWith();
        }
    }

    /// @notice Claims the reward for a specified account and transfers it to the specified recipient.
    /// @param account The account to claim the reward for.
    /// @param recipient The account to receive the reward.
    /// @return The amount of the reward claimed.
    function _getReward(address account, address recipient) internal virtual updateReward(account) returns (uint256) {
        Info storage info = _accountInfo[account];
        uint256 reward = info.unclaimedReward; // get the rewards owed to the account
        if (reward != 0) {
            info.unclaimedReward = 0;
            _safeTransferRewardToken(recipient, reward);
            emit RewardPaid(account, recipient, reward);
        }
        return reward;
    }

    /// @notice Safely transfers the reward tokens to the specified recipient.
    /// @dev Inherited contracts may override this function to implement custom transfer logic.
    /// @param to The recipient address.
    /// @param amount The amount of reward tokens to transfer.
    function _safeTransferRewardToken(address to, uint256 amount) internal virtual {
        rewardToken.safeTransfer(to, amount);
    }

    /// @notice Stakes tokens in the vault for a specified account.
    /// @param account The account to stake the tokens for.
    /// @param amount The amount of tokens to stake.
    function _stake(address account, uint256 amount) internal virtual {
        if (amount == 0) StakeAmountIsZero.selector.revertWith();

        // set the reward rate after the first stake if there are undistributed rewards
        if (totalSupply == 0 && undistributedRewards > 0) {
            _setRewardRate();
        }

        // update the rewards for the account after `rewardRate` is updated
        _updateReward(account);

        unchecked {
            uint256 totalSupplyBefore = totalSupply; // cache storage read
            uint256 totalSupplyAfter = totalSupplyBefore + amount;
            // `<=` and `<` are equivalent here but the former is cheaper
            if (totalSupplyAfter <= totalSupplyBefore) TotalSupplyOverflow.selector.revertWith();
            totalSupply = totalSupplyAfter;
            // `totalSupply` would have overflowed first because `totalSupplyBefore` >= `_accountInfo[account].balance`
            _accountInfo[account].balance += amount;
        }
        _safeTransferFromStakeToken(msg.sender, amount);
        emit Staked(account, amount);
    }

    /// @notice Safely transfers staking tokens from the sender to the contract.
    /// @dev Inherited contracts may override this function to implement custom transfer logic.
    /// @param from The address to transfer the tokens from.
    /// @param amount The amount of tokens to transfer.
    function _safeTransferFromStakeToken(address from, uint256 amount) internal virtual {
        stakeToken.safeTransferFrom(from, address(this), amount);
    }

    /// @notice Withdraws staked tokens from the vault for a specified account.
    /// @param account The account to withdraw the tokens for.
    /// @param amount The amount of tokens to withdraw.
    function _withdraw(address account, uint256 amount) internal virtual {
        if (amount == 0) WithdrawAmountIsZero.selector.revertWith();

        // update the rewards for the account before the balance is updated
        _updateReward(account);

        unchecked {
            Info storage info = _accountInfo[account];
            uint256 balanceBefore = info.balance; // cache storage read
            if (balanceBefore < amount) InsufficientStake.selector.revertWith();
            info.balance = balanceBefore - amount;
            // underflow not possible because `totalSupply` >= `balanceBefore` >= `amount`
            totalSupply -= amount;
        }
        if (totalSupply == 0 && block.timestamp < periodFinish) {
            undistributedRewards += _computeLeftOverReward();
        }
        _safeTransferStakeToken(msg.sender, amount);
        emit Withdrawn(account, amount);
    }

    /// @notice Safely transfers staking tokens to the specified recipient.
    /// @param to The recipient address.
    /// @param amount The amount of tokens to transfer.
    function _safeTransferStakeToken(address to, uint256 amount) internal virtual {
        stakeToken.safeTransfer(to, amount);
    }

    function _setRewardRate() internal virtual {
        uint256 _rewardsDuration = rewardsDuration; // cache storage read
        uint256 _rewardRate = undistributedRewards / _rewardsDuration;
        rewardRate = _rewardRate;
        periodFinish = block.timestamp + _rewardsDuration;
        undistributedRewards -= _rewardRate * _rewardsDuration;
    }

    function _updateReward(address account) internal virtual {
        uint256 _rewardPerToken = rewardPerToken(); // cache result
        rewardPerTokenStored = _rewardPerToken;
        // record the last time the rewards were updated
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            Info storage info = _accountInfo[account];
            (info.unclaimedReward, info.rewardsPerTokenPaid) = (earned(account), _rewardPerToken);
        }
    }

    /// @dev Allows to change `rewardsDuration` during the reward cycle, not affecting `rewardRate` which
    /// will be updated upon next `_notifyRewardAmount()` call.
    function _setRewardsDuration(uint256 _rewardsDuration) internal virtual {
        if (_rewardsDuration == 0) RewardsDurationIsZero.selector.revertWith();
        rewardsDuration = _rewardsDuration;
        emit RewardsDurationUpdated(_rewardsDuration);
    }

    /// @notice Returns the left over amount scaled by PRECISION.
    function _computeLeftOverReward() internal view returns (uint256 leftOver) {
        uint256 remainingTime;
        unchecked {
            remainingTime = periodFinish - block.timestamp;
        }
        leftOver = rewardRate * remainingTime;
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                          GETTERS                           */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    function balanceOf(address account) public view virtual returns (uint256) {
        return _accountInfo[account].balance;
    }

    function rewards(address account) public view virtual returns (uint256) {
        return _accountInfo[account].unclaimedReward;
    }

    function userRewardPerTokenPaid(address account) public view virtual returns (uint256) {
        return _accountInfo[account].rewardsPerTokenPaid;
    }

    function lastTimeRewardApplicable() public view virtual returns (uint256) {
        return FixedPointMathLib.min(block.timestamp, periodFinish);
    }

    /// @dev Gives current reward per token, result is scaled by PRECISION.
    function rewardPerToken() public view virtual returns (uint256) {
        uint256 _totalSupply = totalSupply; // cache storage read
        if (_totalSupply == 0) return rewardPerTokenStored;
        uint256 timeDelta;
        unchecked {
            timeDelta = lastTimeRewardApplicable() - lastUpdateTime;
        }
        // computes reward per token by rounding it down to avoid reverting '_getReward' with insufficient rewards
        uint256 _newRewardPerToken = FixedPointMathLib.fullMulDiv(rewardRate, timeDelta, _totalSupply);
        return rewardPerTokenStored + _newRewardPerToken;
    }

    function earned(address account) public view virtual returns (uint256) {
        Info storage info = _accountInfo[account];
        (uint256 balance, uint256 unclaimedReward, uint256 rewardsPerTokenPaid) =
            (info.balance, info.unclaimedReward, info.rewardsPerTokenPaid);
        uint256 rewardPerTokenDelta;
        unchecked {
            rewardPerTokenDelta = rewardPerToken() - rewardsPerTokenPaid;
        }
        return unclaimedReward + FixedPointMathLib.fullMulDiv(balance, rewardPerTokenDelta, PRECISION);
    }

    function getRewardForDuration() public view virtual returns (uint256) {
        return FixedPointMathLib.fullMulDiv(rewardRate, rewardsDuration, PRECISION);
    }
}

src/extras/IPriceOracle.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.26;

/// @title Provide asset prices in USD with WAD precision
/// @dev Generic interface that wraps the Pyth oracle
interface IPriceOracle {
    /// @dev TBD whether to also return a confidence interval.
    struct Data {
        // Price with WAD precision
        uint256 price;
        // Unix timestamp describing when the price was published
        uint256 publishTime;
    }

    // Signature: 0xd92e233d
    error ZeroAddress();
    error UnavailableData(address asset);

    /// @notice Returns the price in USD.
    /// @dev Reverts if the price has not been recently updated (implementation defined).
    /// @param asset The asset of which to fetch the price.
    /// @return data
    function getPrice(address asset) external view returns (Data memory data);

    /// @notice Returns the price without any sanity checks.
    /// @dev This function returns the most recent price update in this contract without any recency checks.
    /// This function is unsafe as the returned price update may be arbitrarily far in the past.
    ///
    /// Users of this function should check the `publishTime` to ensure that the returned price is
    /// sufficiently recent for their application. If you are considering using this function, it may be
    /// safer / easier to use either `getPrice` or `getPriceNoOlderThan`.
    /// @return data
    function getPriceUnsafe(address asset) external view returns (Data memory data);

    /// @notice Returns the price that is no older than `age` seconds of the current time.
    /// @dev This function is a sanity-checked version of `getPriceUnsafe` which is useful in
    /// applications that require a sufficiently-recent price. Reverts if the price wasn't updated sufficiently
    /// recently.
    /// @return data
    function getPriceNoOlderThan(address asset, uint256 age) external view returns (Data memory data);

    /// @notice Returns whether a price is available or not.
    function priceAvailable(address asset) external view returns (bool);
}

src/honey/CollateralVault.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { PausableUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import { ERC20 } from "solady/src/tokens/ERC20.sol";
import { ERC4626 } from "solady/src/tokens/ERC4626.sol";
import { SafeTransferLib } from "solady/src/utils/SafeTransferLib.sol";

import { Utils } from "../libraries/Utils.sol";
import { IHoneyErrors } from "./IHoneyErrors.sol";

/// @notice This is the ERC4626 vault for the collateral assets to mint Honey.
/// @author Berachain Team
contract CollateralVault is ERC4626, PausableUpgradeable, IHoneyErrors {
    using Utils for bytes4;

    event CustodyInfoSet(bool indexed isCustodyVault, address indexed custodyAddress);

    ERC20 private _vaultAsset;
    string private _name;
    string private _symbol;
    /// @notice The address of the honey factory that created this vault.
    address public factory;

    /// @notice The custody info for the vault.
    CustodyInfo public custodyInfo;

    /// @notice The custody info struct.
    /// @param isCustodyVault Whether the vault is a custody vault.
    /// @param custodyAddress The address of the custody. 0 in case of no custody.
    struct CustodyInfo {
        bool isCustodyVault;
        address custodyAddress;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address asset_, address _factory) external initializer {
        __CollateralVault_init(asset_, _factory);
    }

    function __CollateralVault_init(address asset_, address _factory) internal onlyInitializing {
        __Pausable_init();
        // Check for zero addresses.
        // No need to check for zero asset address,
        // _asset.name(), _asset.symbol() will revert with `EvmError` if asset is zero address.
        if (_factory == address(0)) ZeroAddress.selector.revertWith();
        factory = _factory;
        ERC20 _asset = ERC20(asset_);
        _vaultAsset = _asset;
        _name = string.concat(_asset.name(), "Vault");
        _symbol = string.concat(_asset.symbol(), "Vault");
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                         MODIFIERS                          */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @dev Throws if called by any account other than the factory.
    modifier onlyFactory() {
        _checkFactory();
        _;
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                       ADMIN FUNCTIONS                      */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @notice Pauses the vault.
    /// @dev Only the factory can call this function.
    function pause() external onlyFactory {
        _pause();
    }

    /// @notice Unpauses the vault.
    /// @dev Only the factory can call this function.
    function unpause() external onlyFactory {
        _unpause();
    }

    /**
     * @dev A wrapper to ERC4626.deposit that only VaultAdmin can call.
     * @dev It is a protection against inflation attacks,
     * @dev in which only VaultAdmin can mint/burn shares.
     * @param assets The assets to deposit into the vault to receive shares.
     * @param receiver The address that will receive the shares.
     * @return shares The shares minted to deposit the assets.
     */
    function deposit(uint256 assets, address receiver) public override onlyFactory whenNotPaused returns (uint256) {
        return super.deposit(assets, receiver);
    }

    /**
     * @dev A wrapper to ERC4626.mint that only VaultAdmin can call.
     * @dev It is a protection against inflation attacks,
     * @dev in which only VaultAdmin can mint/burn shares.
     * @param shares The exact shares to mint by depositing the assets.
     * @param receiver The address that will receive the shares.
     * @return assets The assets required to mint the shares.
     */
    function mint(uint256 shares, address receiver) public override onlyFactory whenNotPaused returns (uint256) {
        return super.mint(shares, receiver);
    }

    /**
     * @notice Set the custody info for the vault.
     * @dev Only the factory can call this function.
     * @dev To change the custody vault, the factory must first remove the existing custody vault.
     * @param _isCustodyVault Whether the vault is a custody vault.
     * @param _custodyAddress The address of the custody.
     */
    function setCustodyInfo(bool _isCustodyVault, address _custodyAddress) external onlyFactory {
        if (_isCustodyVault && _custodyAddress == address(0)) ZeroAddress.selector.revertWith();
        CustodyInfo memory _custodyInfo = custodyInfo;

        // set custody info for new custody vault
        if (_isCustodyVault && _custodyInfo.custodyAddress == address(0)) {
            SafeTransferLib.safeTransfer(asset(), _custodyAddress, ERC20(asset()).balanceOf(address(this)));
            custodyInfo = CustodyInfo(_isCustodyVault, _custodyAddress);
            emit CustodyInfoSet(_isCustodyVault, _custodyAddress);
            return;
        }

        // allow to remove custody feature for existing custody vault
        if (!_isCustodyVault && _custodyInfo.custodyAddress == _custodyAddress) {
            SafeTransferLib.safeTransferFrom(
                asset(), _custodyAddress, address(this), ERC20(asset()).balanceOf(_custodyAddress)
            );
            custodyInfo = CustodyInfo(_isCustodyVault, address(0));
            emit CustodyInfoSet(_isCustodyVault, address(0));
            return;
        }
        // revert if any other combination of inputs
        else {
            InvalidCustodyInfoInput.selector.revertWith();
        }
    }

    /**
     * @dev A wrapper to ERC4626.withdraw that only VaultAdmin can call.
     * @dev It is a protection against inflation attacks,
     * @dev in which only VaultAdmin can mint/burn shares.
     * @param assets The exact assets to withdraw from the vault.
     * @param receiver The address that will receive the assets.
     * @param owner The address that will burn the shares.
     * @return shares The shares burned to withdraw the assets.
     */
    function withdraw(
        uint256 assets,
        address receiver,
        address owner
    )
        public
        override
        onlyFactory
        whenNotPaused
        returns (uint256)
    {
        return super.withdraw(assets, receiver, owner);
    }

    /**
     * @dev A wrapper to ERC4626.redeem that only VaultAdmin can call.
     * @dev It is a protection against inflation attacks,
     * @dev in which only VaultAdmin can mint/burn shares.
     * @param shares The shares to redeem for the assets.
     * @param receiver The address that will receive the assets.
     * @param owner The address that will burn the shares.
     * @return assets The assets redeemed from the vault.
     */
    function redeem(
        uint256 shares,
        address receiver,
        address owner
    )
        public
        override
        onlyFactory
        whenNotPaused
        returns (uint256)
    {
        return super.redeem(shares, receiver, owner);
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                          GETTERS                           */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    function name() public view override returns (string memory) {
        return _name;
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function asset() public view virtual override returns (address) {
        return address(_vaultAsset);
    }

    function convertToAssets(uint256 shares) public view override returns (uint256) {
        return _convertToAssets(shares);
    }

    function convertToShares(uint256 assets) public view override returns (uint256) {
        return _convertToShares(assets);
    }

    /**
     * @dev An implementation of ERC4626.totalAssets to avoid inflation attacks,
     * @dev which returns the total assets that VaultAdmin transferred to the vault.
     */
    function totalAssets() public view override returns (uint256) {
        // This is another layer of protection against inflation attacks.
        // ERC4626 uses this function to calculate the exchange rate.
        // Because only VaultAdmin can mint/burn shares via deposit/mint,
        // the total assets transferred by VaultAdmin into this vault
        // must be always equal to the total supply of the shares.
        // Therefore, the assets/shares exchange rate is always 1.
        // Attackers or users can transfer assets directly into this vault
        // (thus the total assets is always greater than or equal the total supply),
        // but that will not change the total assets returned by this
        // function, thus the exchange rate is not changed.
        //
        // We also need to consider the difference in decimals
        // between the vault and the asset to ensure that
        // 10^assetDecimals asset_wei ~ 10^vaultDecimals vault_wei.
        // asset_wei_totalAssets ~ vault_wei_totalSupply / 10**(vaultDecimals - assetDecimals)
        return _convertToAssets(totalSupply());
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                     INTERNAL FUNCTIONS                     */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @inheritdoc ERC4626
    function _initialConvertToShares(uint256 assets) internal view override returns (uint256 shares) {
        return _convertToShares(assets);
    }

    /// @inheritdoc ERC4626
    function _initialConvertToAssets(uint256 shares) internal view override returns (uint256 assets) {
        return _convertToAssets(shares);
    }

    /**
     * @dev Convert the assets to shares with decimals in consideration,
     * but out of the ERC4626's logic to enforce the fixed exchange rate
     * between Honey, shares, and assets.
     * @param assets The assets to convert to shares.
     * @return shares The shares converted from the assets.
     */
    function _convertToShares(uint256 assets) private view returns (uint256 shares) {
        uint8 vaultDecimals = decimals();
        uint8 assetDecimals = _vaultAsset.decimals();
        uint256 exponent;
        if (vaultDecimals >= assetDecimals) {
            unchecked {
                exponent = vaultDecimals - assetDecimals;
            }
            return assets * (10 ** exponent);
        }
        unchecked {
            exponent = assetDecimals - vaultDecimals;
        }
        return assets / (10 ** exponent);
    }

    /**
     * @dev Convert the assets to shares with decimals in consideration,
     * but out of the ERC4626's logic to enforce the fixed exchange rate
     * between Honey, shares, and assets.
     * @param shares The shares to convert to assets.
     * @return assets The assets converted from the shares.
     */
    function _convertToAssets(uint256 shares) private view returns (uint256 assets) {
        uint8 vaultDecimals = decimals();
        uint8 assetDecimals = _vaultAsset.decimals();
        uint256 exponent;
        if (vaultDecimals >= assetDecimals) {
            unchecked {
                exponent = vaultDecimals - assetDecimals;
            }
            return shares / (10 ** exponent);
        }
        unchecked {
            exponent = assetDecimals - vaultDecimals;
        }
        return shares * (10 ** exponent);
    }

    /// @inheritdoc ERC4626
    function _useVirtualShares() internal pure override returns (bool) {
        return false;
    }

    /// @inheritdoc PausableUpgradeable
    function _requireNotPaused() internal view override {
        if (paused()) VaultPaused.selector.revertWith(asset());
    }

    function _checkFactory() internal view {
        if (msg.sender != factory) {
            NotFactory.selector.revertWith();
        }
    }

    /// @dev This function is called after the deposit is made.
    /// @dev If the vault is a custody vault, it will transfer the assets to the custody.
    function _afterDeposit(uint256 assets, uint256) internal override {
        if (custodyInfo.isCustodyVault) {
            SafeTransferLib.safeTransfer(asset(), custodyInfo.custodyAddress, assets);
        }
    }

    /// @dev This function is called before the withdraw is made.
    /// @dev If the vault is a custody vault, it will pull the assets from the custody.
    function _beforeWithdraw(uint256 assets, uint256) internal override {
        if (custodyInfo.isCustodyVault) {
            SafeTransferLib.safeTransferFrom(asset(), custodyInfo.custodyAddress, address(this), assets);
        }
    }
}

