// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { ERC165 } from "./interfaces/IERC165.sol";
import { IBeaconDeposit } from "./interfaces/IBeaconDeposit.sol";
import { Utils } from "../libraries/Utils.sol";

/**
 * @title DepositContract
 * @author Berachain Team
 * @notice A contract that handles validators deposits.
 * @dev Its events are used by the beacon chain to manage the staking process.
 * @dev This contract does not implement the deposit merkle tree.
 */
contract BeaconDeposit is IBeaconDeposit, ERC165 {
    using Utils for bytes4;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                        CONSTANTS                           */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @notice The minimum amount of `BERA` to deposit i.e 10_000 ether.
    /// @dev Converted to Gwei since our deposit contract denominates in Gwei.
    /// @dev 10_000 ether = 10_000e18, in Gwei: 10_000e18 / 1e9 = 10_000e9 = 10_000 gwei.
    uint64 internal constant MIN_DEPOSIT_AMOUNT_IN_GWEI = 10_000 gwei;

    /// @dev The length of the public key, PUBLIC_KEY_LENGTH bytes.
    uint8 internal constant PUBLIC_KEY_LENGTH = 48;

    /// @dev The length of the signature, SIGNATURE_LENGTH bytes.
    uint8 internal constant SIGNATURE_LENGTH = 96;

    /// @dev The length of the credentials, 1 byte prefix + 11 bytes padding + 20 bytes address = 32 bytes.
    uint8 internal constant CREDENTIALS_LENGTH = 32;

    /// @dev 1 day in seconds.
    /// @dev This is the delay before a new operator can accept a change.
    uint96 private constant ONE_DAY = 86_400;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                           STORAGE                          */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @dev QueuedOperator is a struct that represents an operator address change request.
    struct QueuedOperator {
        uint96 queuedTimestamp;
        address newOperator;
    }

    /// @dev depositCount represents the number of deposits that
    /// have been made to the contract.
    /// @dev The index of the next deposit will use this value.
    uint64 public depositCount;

    /// @dev The hash tree root of the genesis deposits.
    /// @dev Should be set in deployment (predeploy state or constructor).
    bytes32 public genesisDepositsRoot;

    /// @dev The mapping of public keys to operator addresses.
    mapping(bytes => address) private _operatorByPubKey;

    /// @dev The mapping of public keys to operator change requests.
    mapping(bytes => QueuedOperator) public queuedOperator;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                            VIEWS                           */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @inheritdoc ERC165
    function supportsInterface(bytes4 interfaceId) external pure override returns (bool) {
        return interfaceId == type(ERC165).interfaceId || interfaceId == type(IBeaconDeposit).interfaceId;
    }

    /// @inheritdoc IBeaconDeposit
    function getOperator(bytes calldata pubkey) external view returns (address) {
        return _operatorByPubKey[pubkey];
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                            DEPOSIT                          */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @inheritdoc IBeaconDeposit
    function deposit(
        bytes calldata pubkey,
        bytes calldata credentials,
        bytes calldata signature,
        address operator
    )
        external
        payable
    {
        if (pubkey.length != PUBLIC_KEY_LENGTH) {
            InvalidPubKeyLength.selector.revertWith();
        }

        if (credentials.length != CREDENTIALS_LENGTH) {
            InvalidCredentialsLength.selector.revertWith();
        }

        if (signature.length != SIGNATURE_LENGTH) {
            InvalidSignatureLength.selector.revertWith();
        }

        // Set operator on the first deposit.
        // zero `_operatorByPubKey[pubkey]` means the pubkey is not registered.
        if (_operatorByPubKey[pubkey] == address(0)) {
            if (operator == address(0)) {
                ZeroOperatorOnFirstDeposit.selector.revertWith();
            }
            _operatorByPubKey[pubkey] = operator;
            emit OperatorUpdated(pubkey, operator, address(0));
        }
        // If not the first deposit, operator address must be 0.
        // This prevents from the front-running of the first deposit to set the operator.
        else if (operator != address(0)) {
            OperatorAlreadySet.selector.revertWith();
        }

        uint64 amountInGwei = _deposit();

        if (amountInGwei < MIN_DEPOSIT_AMOUNT_IN_GWEI) {
            InsufficientDeposit.selector.revertWith();
        }

        // slither-disable-next-line reentrancy-benign,reentrancy-events
        emit Deposit(pubkey, credentials, amountInGwei, signature, depositCount++);
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                        OPERATOR CHANGE                    */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @inheritdoc IBeaconDeposit
    function requestOperatorChange(bytes calldata pubkey, address newOperator) external {
        // Cache the current operator.
        address currentOperator = _operatorByPubKey[pubkey];
        // Only the current operator can request a change.
        // This will also revert if the pubkey is not registered.
        if (msg.sender != currentOperator) {
            NotOperator.selector.revertWith();
        }
        // Revert if the new operator is zero address.
        if (newOperator == address(0)) {
            ZeroAddress.selector.revertWith();
        }
        QueuedOperator storage qO = queuedOperator[pubkey];
        qO.newOperator = newOperator;
        qO.queuedTimestamp = uint96(block.timestamp);
        emit OperatorChangeQueued(pubkey, newOperator, currentOperator, block.timestamp);
    }

    /// @inheritdoc IBeaconDeposit
    function cancelOperatorChange(bytes calldata pubkey) external {
        // Only the current operator can cancel the change.
        if (msg.sender != _operatorByPubKey[pubkey]) {
            NotOperator.selector.revertWith();
        }
        delete queuedOperator[pubkey];
        emit OperatorChangeCancelled(pubkey);
    }

    /// @inheritdoc IBeaconDeposit
    function acceptOperatorChange(bytes calldata pubkey) external {
        QueuedOperator storage qO = queuedOperator[pubkey];
        (address newOperator, uint96 queuedTimestamp) = (qO.newOperator, qO.queuedTimestamp);

        // Only the new operator can accept the change.
        // This will revert if nothing is queued as newOperator will be zero address.
        if (msg.sender != newOperator) {
            NotNewOperator.selector.revertWith();
        }
        // Check if the queue delay has passed.
        if (queuedTimestamp + ONE_DAY > uint96(block.timestamp)) {
            NotEnoughTime.selector.revertWith();
        }
        // Cache the old operator.
        address oldOperator = _operatorByPubKey[pubkey];
        _operatorByPubKey[pubkey] = newOperator;
        delete queuedOperator[pubkey];
        emit OperatorUpdated(pubkey, newOperator, oldOperator);
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                            INTERNAL                         */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @dev Validates the deposit amount and sends the native asset to the zero address.
    function _deposit() internal virtual returns (uint64) {
        if (msg.value % 1 gwei != 0) {
            DepositNotMultipleOfGwei.selector.revertWith();
        }

        uint256 amountInGwei = msg.value / 1 gwei;
        if (amountInGwei > type(uint64).max) {
            DepositValueTooHigh.selector.revertWith();
        }

        _safeTransferETH(address(0), msg.value);

        return uint64(amountInGwei);
    }

    /**
     * @notice Safely transfers ETH to the given address.
     * @dev From the Solady library.
     * @param to The address to transfer the ETH to.
     * @param amount The amount of ETH to transfer.
     */
    function _safeTransferETH(address to, uint256 amount) internal {
        /// @solidity memory-safe-assembly
        assembly {
            if iszero(call(gas(), to, amount, codesize(), 0x00, codesize(), 0x00)) {
                mstore(0x00, 0xb12d13eb) // `ETHTransferFailed()`.
                revert(0x1c, 0x04)
            }
        }
    }
}

src/pol/BeaconRootsHelper.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { FixedPointMathLib } from "solady/src/utils/FixedPointMathLib.sol";

import { IPOLErrors } from "./interfaces/IPOLErrors.sol";

import { BeaconRoots } from "../libraries/BeaconRoots.sol";
import { SSZ } from "../libraries/SSZ.sol";
import { Utils } from "../libraries/Utils.sol";

/// @title BeaconRootsHelper
/// @author Berachain Team
/// @notice A contract that follows the beacon chain roots (using the EIP-4788 Beacon Roots contract) to
/// maintain a buffer of processed timestamps. The buffer mirrors the buffer used in the Beacon Roots contract
/// for consistency, but is not 1:1 since we are not guaranteed updates to this buffer every block.
/// @notice The contract also allows verifying the pubkey of the proposer of a beacon block.
abstract contract BeaconRootsHelper is IPOLErrors {
    using BeaconRoots for uint64;
    using Utils for bytes4;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                         CONSTANTS                          */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @dev The length of the history buffer in the EIP-4788 Beacon Roots contract.
    uint64 private constant HISTORY_BUFFER_LENGTH = 8191;
    /// @dev The validator registry limit in the Beacon spec.
    uint64 private constant VALIDATOR_REGISTRY_LIMIT = 1 << 40;
    /// @dev The validator pubkey offset of Generalized Index based on the fields of the validator SSZ container in the
    /// Beacon spec.
    uint8 private constant VALIDATOR_PUBKEY_OFFSET = 8;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                          EVENTS                            */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @notice Emitted when the zero validator pubkey Generalized Index is changed.
    event ZeroValidatorPubkeyGIndexChanged(uint64 newZeroValidatorPubkeyGIndex);

    /// @notice Emitted when the proposer index Generalized Index is changed.
    event ProposerIndexGIndexChanged(uint64 newProposerIndexGIndex);

    /// @notice Emitted when a timestamp is successfully processed in the history buffer.
    event TimestampProcessed(uint64 timestamp);

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                          STORAGE                           */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @dev Mapping `timestamp_idx` (timestamp % HISTORY_BUFFER_LENGTH) in the history buffer to the
    /// processed timestamp.
    /// @dev Ensure that the value at `timestamp_idx` is the desired timestamp to process.
    uint64[HISTORY_BUFFER_LENGTH] private _processedTimestampsBuffer;

    /// @notice Generalized Index of the pubkey of the first validator (validator index of 0) in the registry of the
    /// beacon state in the beacon block.
    /// @dev In the Deneb beacon fork on Berachain, this should be 3254554418216960.
    uint64 public zeroValidatorPubkeyGIndex;

    /// @notice Generalized Index of the proposer index in the beacon block.
    /// @dev In the Deneb beacon fork on Berachain, this should be 9.
    uint64 public proposerIndexGIndex;

    /// @dev Storage gap for future upgrades.
    uint256[50] private _gap;

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                            ADMIN                           */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @dev This action should be permissioned to prevent unauthorized actors from modifying inappropriately.
    function setZeroValidatorPubkeyGIndex(uint64 _zeroValidatorPubkeyGIndex) public virtual {
        zeroValidatorPubkeyGIndex = _zeroValidatorPubkeyGIndex;
        emit ZeroValidatorPubkeyGIndexChanged(_zeroValidatorPubkeyGIndex);
    }

    /// @dev This action should be permissioned to prevent unauthorized actors from modifying inappropriately.
    function setProposerIndexGIndex(uint64 _proposerIndexGIndex) public virtual {
        proposerIndexGIndex = _proposerIndexGIndex;
        emit ProposerIndexGIndexChanged(_proposerIndexGIndex);
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                  EXTERNAL VIEW FUNCTIONS                   */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @notice Returns whether a timestamp is in the history buffer and can still be processed.
    /// @param timestamp The timestamp to search for.
    /// @return actionable Whether the timestamp is in the history buffer and it has not yet been processed.
    function isTimestampActionable(uint64 timestamp) external view returns (bool) {
        // First check if the timestamp is in the Beacon Roots history buffer.
        if (!timestamp.isParentBlockRootAt()) return false;

        // If we know the timestamp is in the buffer, return if the timestamp has not been processed.
        uint64 timestampIndex = timestamp % HISTORY_BUFFER_LENGTH;
        return _processedTimestampsBuffer[timestampIndex] != timestamp;
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                     INTERNAL FUNCTIONS                     */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @notice Processes the timestamp in the history buffer.
    /// @param timestamp The timestamp to process in the history buffer.
    /// @dev Reverts if the timestamp is not available in the Beacon Roots history buffer.
    /// @dev Reverts if the timestamp is available in the Beacon Roots history buffer but has already been processed.
    /// @return parentBeaconBlockRoot The parent beacon block root at the given timestamp.
    function _processTimestampInBuffer(uint64 timestamp) internal returns (bytes32 parentBeaconBlockRoot) {
        // First enforce the timestamp is in the Beacon Roots history buffer, reverting if not found.
        parentBeaconBlockRoot = timestamp.getParentBlockRootAt();

        // Mark the in buffer timestamp as processed if it has not been processed yet.
        uint64 timestampIndex = timestamp % HISTORY_BUFFER_LENGTH;
        if (timestamp == _processedTimestampsBuffer[timestampIndex]) TimestampAlreadyProcessed.selector.revertWith();
        _processedTimestampsBuffer[timestampIndex] = timestamp;

        // Emit the event that the timestamp has been processed.
        emit TimestampProcessed(timestamp);
    }

    /// @notice Verifies the proposer index is in the beacon block, reverting if the proof is invalid.
    /// @param beaconBlockRoot `bytes32` root of the beacon block.
    /// @param proposerIndexProof `bytes32[]` proof of the proposer index in the beacon block.
    /// @param proposerIndex `uint64` proposer index to verify.
    function _verifyProposerIndexInBeaconBlock(
        bytes32 beaconBlockRoot,
        bytes32[] calldata proposerIndexProof,
        uint64 proposerIndex
    )
        internal
        view
    {
        bytes32 proposerIndexRoot = SSZ.uint64HashTreeRoot(proposerIndex);

        if (!SSZ.verifyProof(proposerIndexProof, beaconBlockRoot, proposerIndexRoot, proposerIndexGIndex)) {
            InvalidProof.selector.revertWith();
        }
    }

    /// @notice Verifies the validator pubkey is in the registry of beacon state at the given validator index,
    /// reverting if the proof is invalid.
    /// @param beaconBlockRoot `bytes32` root of the beacon block.
    /// @param validatorPubkeyProof `bytes32[]` proof of the validator pubkey in the beacon block.
    /// @param validatorPubkey `bytes` 40 byte validator pubkey to verify.
    /// @param validatorIndex `uint64` validator index in the validator registry of the beacon state.
    function _verifyValidatorPubkeyInBeaconBlock(
        bytes32 beaconBlockRoot,
        bytes32[] calldata validatorPubkeyProof,
        bytes calldata validatorPubkey,
        uint64 validatorIndex
    )
        internal
        view
    {
        if (validatorIndex >= VALIDATOR_REGISTRY_LIMIT) {
            IndexOutOfRange.selector.revertWith();
        }

        bytes32 validatorPubkeyRoot = SSZ.validatorPubkeyHashTreeRoot(validatorPubkey);
        uint256 gIndex = zeroValidatorPubkeyGIndex + (VALIDATOR_PUBKEY_OFFSET * validatorIndex);

        if (!SSZ.verifyProof(validatorPubkeyProof, beaconBlockRoot, validatorPubkeyRoot, gIndex)) {
            InvalidProof.selector.revertWith();
        }
    }
}

src/pol/FeeCollector.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { PausableUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import { AccessControlUpgradeable } from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import { Utils } from "../libraries/Utils.sol";
import { IFeeCollector } from "./interfaces/IFeeCollector.sol";
import { BGTStaker } from "./BGTStaker.sol";

/**
 * @title FeeCollector
 * @author Berachain Team
 * @notice The Fee Collector contract is responsible for collecting fees from Berachain Dapps and
 * auctioning them for a Payout token which then is distributed among the BGT stakers.
 * @dev This contract is inspired by the Uniswap V3 Factory Owner contract.
 * https://github.com/uniswapfoundation/UniStaker/blob/main/src/V3FactoryOwner.sol
 */
contract FeeCollector is IFeeCollector, PausableUpgradeable, AccessControlUpgradeable, UUPSUpgradeable {
    using SafeERC20 for IERC20;
    using Utils for bytes4;

    /// @notice The MANAGER role.
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    /// @notice The PAUSER role
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    /// @inheritdoc IFeeCollector
    address public payoutToken;

    /// @inheritdoc IFeeCollector
    uint256 public queuedPayoutAmount;

    /// @inheritdoc IFeeCollector
    uint256 public payoutAmount;

    /// @inheritdoc IFeeCollector
    address public rewardReceiver;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address governance,
        address _payoutToken,
        address _rewardReceiver,
        uint256 _payoutAmount
    )
        external
        initializer
    {
        __AccessControl_init();
        __Pausable_init();
        __UUPSUpgradeable_init();
        if (governance == address(0) || _payoutToken == address(0) || _rewardReceiver == address(0)) {
            ZeroAddress.selector.revertWith();
        }
        if (_payoutAmount == 0) PayoutAmountIsZero.selector.revertWith();

        _grantRole(DEFAULT_ADMIN_ROLE, governance);
        // Allow the MANAGER to control the PAUSER_ROLE
        _setRoleAdmin(PAUSER_ROLE, MANAGER_ROLE);

        payoutToken = _payoutToken;
        payoutAmount = _payoutAmount;
        rewardReceiver = _rewardReceiver;

        emit PayoutAmountSet(0, _payoutAmount);
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                       ADMIN FUNCTIONS                      */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    function _authorizeUpgrade(address newImplementation) internal override onlyRole(DEFAULT_ADMIN_ROLE) { }

    /// @inheritdoc IFeeCollector
    function queuePayoutAmountChange(uint256 _newPayoutAmount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_newPayoutAmount == 0) PayoutAmountIsZero.selector.revertWith();
        emit QueuedPayoutAmount(_newPayoutAmount, payoutAmount);
        queuedPayoutAmount = _newPayoutAmount;
    }

    /*´:°•.°+.*•´.*:˚.°*.˚•´.°:°•.°•.*•´.*:˚.°*.˚•´.°:°•.°+.*•´.*:*/
    /*                       WRITE FUNCTIONS                      */
    /*.•°:°.´+˚.*°.˚:*.´•*.+°.•°:´*.´•*.•°.•°:°.´:•˚°.*°.˚:*.´+°.•*/

    /// @inheritdoc IFeeCollector
    function claimFees(address _recipient, address[] calldata _feeTokens) external whenNotPaused {
        // Transfer the payout amount of the payout token to the BGTStaker contract from msg.sender.
        IERC20(payoutToken).safeTransferFrom(msg.sender, rewardReceiver, payoutAmount);
        // Notify that the reward amount has been updated.
        BGTStaker(rewardReceiver).notifyRewardAmount(payoutAmount);
        // From all the specified fee tokens, transfer them to the recipient.
        for (uint256 i; i < _feeTokens.length;) {
            address feeToken = _feeTokens[i];
            uint256 feeTokenAmountToTransfer = IERC20(feeToken).balanceOf(address(this));
            IERC20(feeToken).safeTransfer(_recipient, feeTokenAmountToTransfer);
            emit FeesClaimed(msg.sender, _recipient, feeToken, feeTokenAmountToTransfer);
            unchecked {
                ++i;
            }
        }
        emit FeesClaimed(msg.sender, _recipient);

        if (queuedPayoutAmount != 0) _setPayoutAmount();
    }

    /// @inheritdoc IFeeCollector
    function donate(uint256 amount) external whenNotPaused {
        // donate amount should be at least payoutAmount to notify the reward receiver.
        if (amount < payoutAmount) DonateAmountLessThanPayoutAmount.selector.revertWith();

        // Directly send the fees to the reward receiver.
        IERC20(payoutToken).safeTransferFrom(msg.sender, rewardReceiver, amount);
        BGTStaker(rewardReceiver).notifyRewardAmount(amount);

        emit PayoutDonated(msg.sender, amount);
    }

    /// @inheritdoc IFeeCollector
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @inheritdoc IFeeCollector
    function unpause() external onlyRole(MANAGER_ROLE) {
        _unpause();
    }

    /// @notice Set the payout amount to the queued payout amount
    function _setPayoutAmount() internal {
        emit PayoutAmountSet(payoutAmount, queuedPayoutAmount);
        payoutAmount = queuedPayoutAmount;
        queuedPayoutAmount = 0;
    }
}

src/pol/POLDeployer.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { Create2Deployer } from "../base/Create2Deployer.sol";
import { BeraChef } from "./rewards/BeraChef.sol";
import { RewardVault } from "./rewards/RewardVault.sol";
import { RewardVaultFactory } from "./rewards/RewardVaultFactory.sol";
import { BlockRewardController } from "./rewards/BlockRewardController.sol";
import { Distributor } from "./rewards/Distributor.sol";

/// @title POLDeployer
/// @author Berachain Team
/// @notice The POLDeployer contract is responsible for deploying the PoL contracts.
contract POLDeployer is Create2Deployer {
    uint8 internal constant maxNumWeightsPerRewardAllocation = 10;

    /// @dev Generalized Index of the pubkey of the first validator (validator index of 0) in the registry of the
    /// beacon state in the beacon block on the Deneb beacon fork on Berachain.
    uint64 internal constant ZERO_VALIDATOR_PUBKEY_G_INDEX = 3_254_554_418_216_960;

    /// @dev Generalized Index of the proposer index in the beacon block on the Deneb beacon fork on Berachain.
    uint64 internal constant PROPOSER_INDEX_G_INDEX = 9;

    /// @notice The address of the BeaconDeposit contract.
    /// @dev This is a placeholder address. defined here instead of constructor to avoid stack too deep error.
    address internal constant BEACON_DEPOSIT = 0x4242424242424242424242424242424242424242;

    /// @notice The BeraChef contract.
    // solhint-disable-next-line immutable-vars-naming
    BeraChef public immutable beraChef;

    /// @notice The BlockRewardController contract.
    // solhint-disable-next-line immutable-vars-naming
    BlockRewardController public immutable blockRewardController;

    /// @notice The RewardVaultFactory contract.
    // solhint-disable-next-line immutable-vars-naming
    RewardVaultFactory public immutable rewardVaultFactory;

    /// @notice The Distributor contract.
    // solhint-disable-next-line immutable-vars-naming
    Distributor public immutable distributor;

    constructor(
        address bgt,
        address governance,
        uint256 beraChefSalt,
        uint256 blockRewardControllerSalt,
        uint256 distributorSalt,
        uint256 rewardVaultFactorySalt
    ) {
        // deploy the BeraChef implementation
        address beraChefImpl = deployWithCreate2(0, type(BeraChef).creationCode);
        // deploy the BeraChef proxy
        beraChef = BeraChef(deployProxyWithCreate2(beraChefImpl, beraChefSalt));

        // deploy the BlockRewardController implementation
        address blockRewardControllerImpl = deployWithCreate2(0, type(BlockRewardController).creationCode);
        // deploy the BlockRewardController proxy
        blockRewardController =
            BlockRewardController(deployProxyWithCreate2(blockRewardControllerImpl, blockRewardControllerSalt));

        // deploy the Distributor implementation
        address distributorImpl = deployWithCreate2(0, type(Distributor).creationCode);
        // deploy the Distributor proxy
        distributor = Distributor(deployProxyWithCreate2(distributorImpl, distributorSalt));

        // deploy the RewardVault implementation
        address vaultImpl = deployWithCreate2(0, type(RewardVault).creationCode);
        address rewardVaultFactoryImpl = deployWithCreate2(0, type(RewardVaultFactory).creationCode);
        // deploy the RewardVaultFactory proxy
        rewardVaultFactory = RewardVaultFactory(deployProxyWithCreate2(rewardVaultFactoryImpl, rewardVaultFactorySalt));

        // initialize the contracts
        beraChef.initialize(
            address(distributor),
            address(rewardVaultFactory),
            governance,
            BEACON_DEPOSIT,
            maxNumWeightsPerRewardAllocation
        );
        blockRewardController.initialize(bgt, address(distributor), BEACON_DEPOSIT, governance);
        distributor.initialize(
            address(beraChef),
            bgt,
            address(blockRewardController),
            governance,
            ZERO_VALIDATOR_PUBKEY_G_INDEX,
            PROPOSER_INDEX_G_INDEX
        );
        rewardVaultFactory.initialize(bgt, address(distributor), BEACON_DEPOSIT, governance, vaultImpl);
    }
}

src/pol/interfaces/IBGT.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.26;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { IVotes } from "@openzeppelin/contracts/governance/utils/IVotes.sol";
import { IPOLErrors } from "./IPOLErrors.sol";

interface IBGT is IPOLErrors, IERC20, IERC20Metadata, IVotes {
    /**
     * @notice Emitted when the minter address is changed.
     * @param previous The address of the previous minter.
     * @param current The address of the current minter.
     */
    event MinterChanged(address indexed previous, address indexed current);

    /**
     * @notice Emitted when the Staker address is changed.
     * @param previous The address of the previous Staker.
     * @param current The address of the current Staker.
     */
    event StakerChanged(address indexed previous, address indexed current);

    /**
     * @notice Emitted when an address is approved to send BGT.
     * @param sender The address of the sender.
     * @param approved Whether the sender is approved or not.
     */
    event SenderWhitelisted(address indexed sender, bool approved);

    /**
     * @notice Emitted when sender queues a new boost for a validator with an amount of BGT
     * @param user The address of the user.
     * @param pubkey The pubkey of the validator to be queued for boost.
     * @param amount The amount of BGT enqueued for boosting during function call.
     */
    event QueueBoost(address indexed user, bytes indexed pubkey, uint128 amount);

    /**
     * @notice Emitted when sender cancels a queued boost for a validator with an amount of BGT
     * @param user The address of the user.
     * @param pubkey The pubkey of the validator to be canceled from queued boosts.
     * @param amount The amount of BGT to cancel from queued boosts.
     */
    event CancelBoost(address indexed user, bytes indexed pubkey, uint128 amount);

    /**
     * @notice Emitted when sender activates a new boost for a validator
     * @param sender The address of the sender.
     * @param user The address of the user boosting.
     * @param pubkey The pubkey of the validator to be activated for the queued boosts.
     * @param amount The amount of BGT to boost with.
     */
    event ActivateBoost(address indexed sender, address indexed user, bytes indexed pubkey, uint128 amount);

    /**
     * @notice Emitted when an user queues a drop boost for a validator.
     * @param user The address of the user.
     * @param pubkey The pubkey of the validator to remove boost from.
     * @param amount The amount of BGT boost enqueued for dropping boost during function call.
     */
    event QueueDropBoost(address indexed user, bytes indexed pubkey, uint128 amount);

    /**
     * @notice Emitted when an user cancels a queued drop boost for a validator.
     * @param user The address of the user.
     * @param pubkey The pubkey of the validator to cancel drop boost for.
     * @param amount The amount of BGT boost to cancel.
     */
    event CancelDropBoost(address indexed user, bytes indexed pubkey, uint128 amount);

    /**
     * @notice Emitted when sender removes an amount of BGT boost from a validator
     * @param sender The address of the sender.
     * @param user The address of the user dropping boost.
     * @param pubkey The pubkey of the validator to remove boost from.
     * @param amount The amount of BGT boost to remove.
     */
    event DropBoost(address indexed sender, address indexed user, bytes indexed pubkey, uint128 amount);

    /**
     * @notice Emitted when the BGT token is redeemed for the native token.
     * @param from The address of the redeemer.
     * @param receiver The address of the receiver.
     * @param amount The amount of redeemed  BGT (and thus, of received BERA).
     */
    event Redeem(address indexed from, address indexed receiver, uint256 amount);

    /**
     * @notice Emitted when the activate boost delay is changed.
     * @param newDelay The new delay for activating boosts.
     */
    event ActivateBoostDelayChanged(uint32 newDelay);

    /**
     * @notice Emitted when the drop boost delay is changed.
     * @param newDelay The new delay for dropping boosts.
     */
    event DropBoostDelayChanged(uint32 newDelay);

    /**
     * @notice Emitted when the BGT terms and conditions are changed.
     * @param newTermsAndConditions The new terms and conditions.
     */
    event BgtTermsAndConditionsChanged(string newTermsAndConditions);

    /**
     * @notice Returns the BGT staker contract address.
     * @return The address of the staker.
     */
    function staker() external view returns (address);

    /**
     * @notice Returns the activate boost delay.
     * @return The activate boost delay.
     */
    function activateBoostDelay() external view returns (uint32);

    /**
     * @notice Returns the drop boost delay.
     * @return The drop boost delay.
     */
    function dropBoostDelay() external view returns (uint32);

    /**
     * @notice Emitted when the native token exceeding reserves are burnt.
     * @param caller The address of the caller.
     * @param amount The amount of BERA burnt.
     */
    event ExceedingReservesBurnt(address caller, uint256 amount);

    /**
     * @notice Approve an address to send BGT or approve another address to transfer BGT from it.
     * @dev This can only be called by the governance module.
     * @dev BGT should be soul bound to EOAs and only transferable by approved senders.
     * @param sender The address of the sender.
     * @param approved Whether the sender is approved or not.
     */
    function whitelistSender(address sender, bool approved) external;

    /**
     * @notice Mint BGT to the distributor.
     * @dev This can only be called by the minter address, which is set by governance.
     * @param distributor The address of the distributor.
     * @param amount The amount of BGT to mint.
     */
    function mint(address distributor, uint256 amount) external;

    /**
     * @notice Queues a new boost of the validator with an amount of BGT from `msg.sender`.
     * @dev Reverts if `msg.sender` does not have enough unboosted balance to cover amount.
     * @param pubkey The pubkey of the validator to be boosted.
     * @param amount The amount of BGT to use for the queued boost.
     */
    function queueBoost(bytes calldata pubkey, uint128 amount) external;

    /**
     * @notice Cancels a queued boost of the validator removing an amount of BGT for `msg.sender`.
     * @dev Reverts if `msg.sender` does not have enough queued balance to cover amount.
     * @param pubkey The pubkey of the validator to cancel boost for.
     * @param amount The amount of BGT to remove from the queued boost.
     */
    function cancelBoost(bytes calldata pubkey, uint128 amount) external;

    /**
     * @notice Boost the validator with an amount of BGT from `user`.
     * @param user The address of the user boosting.
     * @param pubkey The pubkey of the validator to be boosted.
     * @return bool False if amount is zero or if enough time has not passed, otherwise true.
     */
    function activateBoost(address user, bytes calldata pubkey) external returns (bool);

    /**
     * @notice Queues a drop boost of the validator removing an amount of BGT for sender.
     * @dev Reverts if `user` does not have enough boosted balance to cover amount.
     * @param pubkey The pubkey of the validator to remove boost from.
     * @param amount The amount of BGT to remove from the boost.
     */
    function queueDropBoost(bytes calldata pubkey, uint128 amount) external;

    /**
     * @notice Cancels a queued drop boost of the validator removing an amount of BGT for sender.
     * @param pubkey The pubkey of the validator to cancel drop boost for.
     * @param amount The amount of BGT to remove from the queued drop boost.
     */
    function cancelDropBoost(bytes calldata pubkey, uint128 amount) external;
    /**
     * @notice Drops an amount of BGT from an existing boost of validator by user.
     * @param user The address of the user to drop boost from.
     * @param pubkey The pubkey of the validator to remove boost from.
     * @return bool False if amount is zero or if enough time has not passed, otherwise true.
     */
    function dropBoost(address user, bytes calldata pubkey) external returns (bool);

    /**
     * @notice Returns the amount of BGT queued up to be used by an account to boost a validator.
     * @param account The address of the account boosting.
     * @param pubkey The pubkey of the validator being boosted.
     */
    function boostedQueue(
        address account,
        bytes calldata pubkey
    )
        external
        view
        returns (uint32 blockNumberLast, uint128 balance);

    /**
     * @notice Returns the amount of BGT queued up by an account to drop boost from a validator.
     * @param account The address of the account dropping boost.
     * @param pubkey The pubkey of the validator to drop boost for.
     */
    function dropBoostQueue(
        address account,
        bytes calldata pubkey
    )
        external
        view
        returns (uint32 blockNumberLast, uint128 balance);

    /**
     * @notice Returns the amount of BGT queued up to be used by an account for boosts.
     * @param account The address of the account boosting.
     */
    function queuedBoost(address account) external view returns (uint128);

    /**
     * @notice Returns the amount of BGT used by an account to boost a validator.
     * @param account The address of the account boosting.
     * @param pubkey The pubkey of the validator being boosted.
     */
    function boosted(address account, bytes calldata pubkey) external view returns (uint128);

    /**
     * @notice Returns the amount of BGT used by an account for boosts.
     * @param account The address of the account boosting.
     */
    function boosts(address account) external view returns (uint128);

    /**
     * @notice Returns the amount of BGT attributed to the validator for boosts.
     * @param pubkey The pubkey of the validator being boosted.
     */
    function boostees(bytes calldata pubkey) external view returns (uint128);

    /**
     * @notice Returns the total boosts for all validators.
     */
    function totalBoosts() external view returns (uint128);

    /**
     * @notice Returns the normalized boost power for the validator given outstanding boosts.
     * @dev Used by distributor get validator boost power.
     * @param pubkey The pubkey of the boosted validator.
     */
    function normalizedBoost(bytes calldata pubkey) external view returns (uint256);

    /**
     * @notice Public variable that represents the caller of the mint method.
     * @dev This is going to be the BlockRewardController contract at first.
     */
    function minter() external view returns (address);

    /**
     * @notice Set the minter address.
     * @dev This can only be called by the governance module.
     * @param _minter The address of the minter.
     */
    function setMinter(address _minter) external;

    /**
     * @notice Set the BGT staker contract address.
     * @param _staker The address of the staker.
     */
    function setStaker(address _staker) external;

    /**
     * @notice Set the activate boost delay.
     * @param _activateBoostDelay The new delay for activating boosts.
     */
    function setActivateBoostDelay(uint32 _activateBoostDelay) external;

    /**
     * @notice Set the drop boost delay.
     * @param _dropBoostDelay The new delay for dropping boosts.
     */
    function setDropBoostDelay(uint32 _dropBoostDelay) external;

    /**
     * @notice Redeem the BGT token for the native token at a 1:1 rate.
     * @param receiver The receiver's address who will receive the native token.
     * @param amount The amount of BGT to redeem.
     */
    function redeem(address receiver, uint256 amount) external;

    /**
     * @notice Returns the unboosted balance of an account.
     * @param account The address of the account.
     */
    function unboostedBalanceOf(address account) external view returns (uint256);

    /**
     * @notice Burns the excess BERA held by this contract to redeem BGTs.
     * @dev Since CL at deployment is minting to this contract a quantity of native token sufficient to redeem
     * the upper bound of BGT production range, this contract may hold excess BERA. Can burn BERA exceeding
     * BGT.totalSupply() + outstanding rewards.
     */
    function burnExceedingReserves() external;
}

